library(shiny)
require(data.table)
require(reactable)
require(shinyjs)

base_assumptions<-as.data.table(readxl::read_excel("baseline_assumptions.xlsx"))


function(input, output, session) {
    
    session$onSessionEnded(function(){
        
        stopApp()
        
    })
    
    observeEvent(input$btn_toggle,{
        
        toggle(id="baseline_controls")
        
        
    })
    
    cols_exclude<-c("var","desc")
    
    la_list<-names(base_assumptions)[!names(base_assumptions) %in% cols_exclude]
    
    output$ui_la_select<-renderUI({
        
        selectizeInput("select_la","Select scenario",choices=la_list,multiple=F)
        
    })
    
    proj_res<-lapply(la_list,function(x){
        
        b_values<-base_assumptions[,.SD,.SDcols=c("var",x)]
        
        names(b_values)[names(b_values)==x]<-"value"
        
        list(projections=reactiveValues(baseline=NA,intervention=NA,update_col=NA),
             baseline_values=reactiveValues(baseline=b_values)
        )
        
    })
    
    names(proj_res)<-la_list
    
    inline_control<-function(...){
        
        div(...,style="width: 23%;display: inline-block;")
        
    }
    
    output$ui_baseline_controls<-renderUI({
        
        list(
            inline_control(numericInput("num_nWaiting","Current waiting list size - initials",value=4000)),
        inline_control(numericInput("num_nWaiting_follow","Current waiting list size - 2nd appts",value=1000)),
        inline_control(numericInput("num_refAll_baseline","New referrals per week",value=60)),
        inline_control(numericInput("num_accReferrals_baseline","Acceptance rate (%)",value=50,min=0,max=100)),
        inline_control(numericInput("num_leftReferrals_baseline","People leaving the waiting list per week",value=15)),
        inline_control(numericInput("num_appts_init_baseline","Initial appts available per week",value=20)),
        inline_control(numericInput("num_appts_dna_baseline","Initial appt utilisation rate (%)",min=0,value=90,max=100)),
        inline_control(numericInput("num_appts_follow_baseline","2nd appts available per week",value=10)),
        inline_control(numericInput("num_followup_perc","% of referrals needing 2nd appt",value=50,min=0,max=100)),
        inline_control(numericInput("num_reref_baseline","Re-referral rate (% of rejected referrals)",value=5,min=0,max=100)),
        inline_control(numericInput("num_reref_lag","Average weeks to re-referral",value=12,min=0)),
        inline_control(numericInput("num_wait_min","Minimum waiting time (weeks)",value=6,min=0)),
        inline_control(numericInput("num_wait_max","Maximum waiting time (weeks)",value=52,min=0))
        )
        
    })
    
    outputOptions(output,"ui_baseline_controls",suspendWhenHidden=F)
    
    trigger_switchProjections<-reactiveValues(counter=0)
    
    observeEvent({
        lapply(base_assumptions$var,function(x){input[[x]]})
        },ignoreNULL = T, ignoreInit = T,{
        
        trigger_switchProjections$counter<-isolate(trigger_switchProjections$counter) + 1
        
    })
    
    observeEvent(input$select_la,ignoreInit = T, ignoreNULL=T,{
        
        base_dat<-isolate(proj_res[[input$select_la]]$baseline_values$baseline)
        
        for(x in base_dat$var){
            
            val<-base_dat$value[base_dat$var==x]
            
            updateNumericInput(session,x,value=val)
            
            
            
        }
        
        
    })
    
    
    backlog_calc<-function(row,backlog,dat,followup_backlog){
        
        reref_lag<-dat[["reref_lag"]][row]
        
        acc_rate<-dat[["acc_ref"]][row]/100
        
        new_acc_ref<-dat[["new_ref"]][row] * acc_rate
        
        if(row>reref_lag){
            
            inflow<- new_acc_ref + (dat[["reref"]][row - reref_lag]/100) * (dat[["new_ref"]][row-reref_lag] * (1-acc_rate) )
            
        }else{
            inflow<-new_acc_ref + (dat[["reref"]][1]/100) * (dat[["new_ref"]][1] * (1-acc_rate) )
        }
        
        init_appts<-round(dat[["appt_init"]][row]*(dat[["dna_init"]][row]/100))
        
        outflow<-dat[["left"]][row] + init_appts
        
        new_follow_ups<-round(init_appts * (dat[["followup_perc"]][row]/100))
        
        completed_follow_ups<-dat[["appt_follow"]][row]
        
        list(init_appts_backlog=backlog + inflow - outflow,
             followup_backlog=followup_backlog + new_follow_ups - completed_follow_ups
        )
        
    }
    
    controls_lookup<-list(new_ref=list(title="New referrals\nper week",help_text="Count of new referrals received per week"),
                       acc_ref=list(title="Accepted referrals\nrate (%)",help_text="% of new referrals accepted at triage and added to waiting list"),
                       left=list(title="Number leaving waiting\nlist per week",help_text="Number of referrals leaving waiting list for other reasons than getting an appointment"),
                       dna_init=list(title="Initial appt slots utilisation rate (%)",help_text="% of available initial appointment slots filled with completed appointments"),
                       appt_init=list(title="Initial appt slots available\nper week",help_text="Number of initial appointment slots available per week"),
                       appt_follow=list(title="2nd appt slots available\nper week",help_text="Number of second or follow up appointment slots available per week"),
                       followup_perc=list(title="% requiring 2nd appt",help_text="% of completed initial appts that require a 2nd appointment or followup to confirm diagnosis"),
                       reref=list(title="Re-Referral rate\n(% of rejected referrals)",help_text="% of referrals rejected at triage that will be referred"),
                       reref_lag=list(title="Average weeks to re-referral",help_text="Length of time between a rereferral being rejected at triage and being rereferred to the service")
                       )
    
    la_tracker<-reactiveValues(current=NA)
    
    calc_baseline<-function(){
        
        today<-lubridate::wday(Sys.Date(),week_start=1)
        
        if(today==1){
            
            dt_start<-Sys.Date()
            
        }else{
            
            dt_start<-Sys.Date() + (8-today)
            
        }
        
        base_df<-data.table(week=seq(dt_start,dt_start+(365*3),by="weeks"),
                            new_ref=input$num_refAll_baseline,
                            acc_ref=input$num_accReferrals_baseline,
                            left=input$num_leftReferrals_baseline,
                            appt_init=input$num_appts_init_baseline,
                            appt_follow=input$num_appts_follow_baseline,
                            followup_perc=input$num_followup_perc,
                            reref=input$num_reref_baseline,
                            reref_lag=input$num_reref_lag,
                            dna_init=input$num_appts_dna_baseline,
                            backlog=NA,
                            backlog_follow=NA)
        
        
        init_backlog<-backlog_calc(1,input$num_nWaiting,base_df,followup_backlog = input$num_nWaiting_follow)
        
        base_df$backlog[1]<-init_backlog$init_appts_backlog
        
        base_df$backlog_follow[1]<-init_backlog$followup_backlog
        
        for(x in 2:nrow(base_df)){
            
            backlog_out<-backlog_calc(x,base_df$backlog[x-1],base_df,base_df$backlog_follow[x-1])
            
            base_df$backlog[x]<-backlog_out$init_appts_backlog
            
            base_df$backlog_follow[x]<-backlog_out$followup_backlog
            
        }
        
        base_df$sus_waitlist_size<-(base_df$new_ref*base_df$acc_ref/100)*input$num_wait_min + ((input$num_wait_max-input$num_wait_min)*(base_df$new_ref*base_df$acc_ref/100)/2)
        
        base_df$diff<-base_df$backlog - base_df$sus_waitlist_size
        
        base_df
    }
    
    observeEvent(ignoreNULL=T,ignoreInit = T,
                 {trigger_switchProjections$counter},{
        
        req(isolate(input$select_la))
        
        la<-input$select_la
        
        current_la<-isolate(la_tracker$current)
        
        la_change<-ifelse(is.na(current_la),T,ifelse(!current_la==la,T,F))
        
        base_df<-calc_baseline()
        
        if(!is.data.table(proj_res[[la]]$projections$baseline)|!la_change){
        proj_res[[la]]$projections$baseline<-base_df[,grp:="Baseline"]
        
        proj_res[[input$select_la]]$baseline_values$baseline<-rbindlist(lapply(base_assumptions$var,function(x){data.table(var=x,value=input[[x]])}))
        
        
        }else{
            
            proj_res[[la]]$projections$baseline<-isolate(proj_res[[la]]$projections$baseline)
            
        }
        
        if(!is.data.table(proj_res[[la]]$projections$intervention)|!la_change){

        proj_res[[la]]$projections$intervention<-copy(base_df)[,grp:="Intervention"]
        
        }else{
            
            
            
            proj_res[[la]]$projections$intervention<-isolate(proj_res[[la]]$projections$intervention)
            
        }
        
        if(la_change){
            
            la_tracker$current<-la
            
        }
       
        
    })
    
    output$proj_plot<-renderAmChart4({
        
        req(input$num_nWaiting,input$select_la)
        
        
        req(proj_res[[input$select_la]]$projections$intervention,proj_res[[input$select_la]]$projections$baseline)
        
        
        dat<-rbind(proj_res[[input$select_la]]$projections$intervention,proj_res[[input$select_la]]$projections$baseline)
        
        # ggplot(dat,aes(x=week,y=backlog,grp=grp,colour=grp))+geom_line()+
        #     geom_hline(yintercept=proj_res$baseline$sus_waitlist_size[1],lty="dashed")+
        #     theme_bw() +
        #     scale_y_continuous(limits=c(0,NA))+
        #     ylab("Waiting list size") +
        #     annotate("text", x =min(dat$week) + 365, y =proj_res$baseline$sus_waitlist_size[1] + 100, 
        #              label="Sustainable waiting list size")
        
        dat<-dcast(dat[,.(week,sus_waitlist_size,backlog,grp)],week+sus_waitlist_size~grp,value.var="backlog")
        
        amLineChart(
            data=dat,
            xValue="week",
            yValues=c("Intervention","Baseline","sus_waitlist_size"),
            yValueNames = list(Baseline="'Do nothing' projection",Intervention="'To be' projection",
                               sus_waitlist_size="Sustainable waiting list"),
            yAxis=list(title=amText(text="Waiting list size - Initial appts",fontSize = 16)),
            xAxis="",
            animated=F,
            legend=amLegend(position="right"),
            yLimits=c(0,max(c(dat$Baseline,dat$Intervention))*1.1),
            lineStyle = list(`Baseline`=amLine(color = "#fb9a99"),Intervention=amLine(color="#1f78b4"),
                             sus_waitlist_size=amLine(color="#b2df8a",dash="3,3"))
           
        )
    })
    
    output$proj_tb<-renderReactable({
        
        req(input$num_nWaiting,input$select_la)
        
        
        req(proj_res[[input$select_la]]$projections$intervention,proj_res[[input$select_la]]$projections$baseline,input$num_wait_min,input$num_wait_max)
        
        
        dat<-proj_res[[input$select_la]]$projections$intervention
        
        dat_orig<-proj_res[[input$select_la]]$projections$baseline
        
        #browser()
        year_dates<-c(
            dat$week[1 + 52],
            dat$week[1 + 52*2],
            dat$week[1 + 52*3]
        )
        
        dat<-merge(dat[week %in% year_dates,.(Date=week,`'To be' backlog (target = 0)`=backlog,
                                              `'To be' 2nd appts waiting`=backlog_follow)],
              dat_orig[week %in% year_dates,.(Date=week,`'Do nothing' backlog`=backlog,`'Do nothing' 2nd appts waiting`=backlog_follow)],by="Date"
              )[,`Difference vs Do nothing`:=`'To be' backlog (target = 0)` - `'Do nothing' backlog`
                ][,`Difference vs Do nothing (2nd appts)`:=`'To be' 2nd appts waiting` - `'Do nothing' 2nd appts waiting`
                  ][,.(Date,`'To be' backlog (target = 0)`,`Difference vs Do nothing`,`'To be' 2nd appts waiting`,`Difference vs Do nothing (2nd appts)`)]
        
        names(dat)[2]<-paste0("'To be' initial appts waiting list size (target = ",
                              round(isolate(unique(proj_res[[input$select_la]]$projections$baseline$sus_waitlist_size))),")")
        
        reactable(
            dat,
            columns=list(
                Date=colDef("Projection Date",format=colFormat(date=T))
            ),
            defaultColDef = colDef(format=colFormat(digits=0))
        )
        
        
    })
    
    
    observeEvent(input$btn_reset,{
        
        proj_res[[input$select_la]]$projections$update_col<-NA
        
        proj_res[[input$select_la]]$projections$intervention<-copy(proj_res[[input$select_la]]$projections$baseline)[,grp:="Intervention"]
        
    })
    
    
    lapply(names(controls_lookup),function(x){
        output[[paste0("plot_",x)]]<-renderAmChart4({
            
            require(rAmCharts4)
            
            req(input$num_nWaiting,input$select_la)
            
            req(proj_res[[input$select_la]]$projections$intervention)
            
            chart_df<-proj_res[[input$select_la]]$projections$intervention[,.SD,.SDcols=c("week",x)]
            
            req(chart_df)
            
            if(!is.na(proj_res[[input$select_la]]$projections$update_col)){
                
                req(x %in% proj_res[[input$select_la]]$projections$update_col,cancelOutput = T)
                
            }
            
            yNm<-unname(controls_lookup[names(controls_lookup)==x])
            
            if(grepl("%",yNm)){
                limits=c(0,100)
            }else{
                
                limits<-c(0,max(chart_df[[x]])*2)
            }
            
            lineStyles<-list(a=amLine(color = "#a6cee3"))
            
            names(lineStyles)[1]<-x
            
            amLineChart(
                data=chart_df,
                xValue="week",
                yValues=x,
                yAxis=amText(text=yNm,fontSize = 10),
                xAxis="",
                draggable=T,
                animated=F,
                xLimits=c(min(chart_df$week),max(chart_df$week)),
                yLimits=limits,
                lineStyle = lineStyles
            )
        })
        
        inp_var<-paste0("plot_",x,"_change")
        
        observeEvent(input[[inp_var]],ignoreNULL=T,ignoreInit=T,{
            
            new_dat<-isolate(proj_res[[input$select_la]]$projections$intervention)
            
            
            new_x<-input[[inp_var]]$x - 1
            
            new_y<-round(input[[inp_var]]$y)
            
            var_change<-input[[inp_var]]$variable
            
            new_dat[[var_change]][new_dat$week>=new_x]<-new_y
            
            
            init_backlog<-backlog_calc(1,input$num_nWaiting,new_dat,followup_backlog = input$num_nWaiting_follow)
            
            new_dat$backlog[1]<-init_backlog$init_appts_backlog
            
            new_dat$backlog_follow[1]<-init_backlog$followup_backlog
            
            for(x in 2:nrow(new_dat)){
                
                backlog_out<-backlog_calc(x,new_dat$backlog[x-1],new_dat,new_dat$backlog_follow[x-1])
                
                new_dat$backlog[x]<-backlog_out$init_appts_backlog
                
                new_dat$backlog_follow[x]<-backlog_out$followup_backlog
                
            }
            
            new_dat$diff<-new_dat$backlog - new_dat$sus_waitlist_size
            
            proj_res[[input$select_la]]$projections$intervention<-new_dat
            
            proj_res[[input$select_la]]$projections$update_col<-var_change
            
        })
        
    })
    
    lapply(names(controls_lookup),function(x){
        output[[paste0("ui_",x)]]<-renderUI({
            
            div(class="card",style="display: inline-block; width: 500px;",
                bslib::tooltip(
                    div(style="width: max-content",
                        h4(controls_lookup[[x]]$title,style="display:inline-block; width: max-content"),
                        bsicons::bs_icon("question-circle-fill")
                    ),
                    controls_lookup[[x]]$help_text,placement = "right"),
                amChart4Output(paste0("plot_",x),height=250)
            )
            
            
        })
    })
        
    
    output$btn_dl<-downloadHandler(
        filename = function(){
            
            paste0("waitlist_projections_",as.character(gsub("[^0-9]","",Sys.time())),".xlsx")
            
            
        },
        content = function(file){
            
            outList<-list(
                `Do nothing projection`=proj_res[[input$select_la]]$projections$baseline,
                `To be projection`=proj_res[[input$select_la]]$projections$intervention
            )
            
            writexl::write_xlsx(
                outList,file
            )
            
        }
    )
    
    
}
